"use strict";

function calc() { 
	return getCodeFor([
			"-----",
			"-123-", 
			"-456-", 
			"-789-",
			"-----"]) + " " + 
		getCodeFor([
			"-------",
			"---1---", 
			"--234--", 
			"-56789-", 
			"--ABC--", 
			"---D---",
			"-------"]);
}

function getCodeFor(keyboard) {
	const moves = {"U": [0, -1], "D": [0, 1], "L": [-1, 0], "R": [1, 0]};
	let code = [];
	let [x, y] = getKeyCoord(keyboard, "5");

	input.split("\n").forEach(line => {
		line.split("").forEach(c => {
			let x1 = x + moves[c][0];
			let y1 = y + moves[c][1];

			if (keyboard[y1][x1] !== "-") {
				[x, y] = [x1, y1];
			}
		});

		code.push(keyboard[y][x]);
	});

	return code.join("");
}

function getKeyCoord(keyboard, key) {
	for (let y = 0; y < keyboard.length; ++y) {
		let x = keyboard[y].indexOf(key);
		if (x >= 0) {
			return [x, y];
		}
	}

	throw new Error("Can't find key: " + key);
}

